import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

function HomePage(props) {
  let data = [
    {
      value: "There are total 2 quetions.",
    },
    {
      value: "1 minute for each quetion.",
    },
    {
      value: "Minimum 1 quetion correct to pass the exam otherwise fail.",
    },
    {
      value:
        "Once you presses next then you cannot go to the previous quetion.",
    },
    {
      value:
        "If 1 min time complete then you automatically go to the next quetion. ",
    },
    {
      value: "Make sure you have good internet connection.",
    },
    {
      value:
        "If you press browser's back button then your exam will terminated with score 0. ",
    },
  ];
  const [fields, setFields] = useState([]);
  let check = true;

  useEffect(() => {
    fetch("http://127.0.0.1:3000/instructions")
      .then((res) => res.json())
      .then((results) => {
        setFields(results);
      });
  }, []);
  let lenoffields = fields.length;
  if (lenoffields != 0) {
    data = fields[0].line;
    check = false;
  }
  return (
    <div className="container">
      <div className="box2">
        <h2>Instructions</h2>
        {data.map((field, idx) => {
          return (
            <div key={`${idx}`}>
              <div>{`${idx + 1}. ${field.value}`}</div>
            </div>
          );
        })}
        <button
          className="startbtn"
          onClick={() => {
            return (
              <Link
                to="/test"
                style={{ textDecoration: "none", color: "black" }}
              />
            );
          }}
        >
          Start Test
        </button>
      </div>
      {check ? (
        <Link
          to="customtest"
          style={{
            textDecoration: "none",
            color: "black",
            fontSize: 14,
            position: "absolute",
            bottom: 15,
          }}
        >
          Make Your Own Custom Test
        </Link>
      ) : (
        <div></div>
      )}
    </div>
  );
}

export default HomePage;
