import React, { useState } from "react";

function Quetions(props) {
  const data = [
    {
      quetion: "Which of the following commands will create a list?",
      option1: "list1 = list()",
      option2: "list1 = []",
      option3: "list1 = list([1, 2, 3])",
      option4: "all of the mentioned",
      ans: "all of the mentioned",
    },
    {
      quetion: "What is the output when we execute list(“hello”)?",
      option1: "[‘h’, ‘e’, ‘l’, ‘l’, ‘o’]",
      option2: " [‘hello’]",
      option3: " [‘llo’]",
      option4: "[‘olleh’]",
      ans: "[‘h’, ‘e’, ‘l’, ‘l’, ‘o’]",
    },
  ];
  const [res, setRes] = useState("");
  const [score, setScore] = useState(0);
  const [radiochecked1, setRadiochecked1] = useState(false);
  const [radiochecked2, setRadiochecked2] = useState(false);
  const [radiochecked3, setRadiochecked3] = useState(false);
  const [radiochecked4, setRadiochecked4] = useState(false);
  const [i, setI] = useState(0);

  const handlechange = (event) => {
    setRes(event.target.value);
  };
  const handlenext = () => {
    if (data[i].ans === res) {
      setScore(score + 1);
    }
    // setI(i + 1);
    setRadiochecked1(false);
    setRadiochecked2(false);
    setRadiochecked3(false);
    setRadiochecked4(false);
  };

  return (
    <div>
      <div>{data[i].quetion}</div>
      <form className="options">
        <div>
          <input
            type="radio"
            name="category"
            checked={radiochecked1}
            onClick={() => setRadiochecked1(true)}
            value={data[i].option1}
            onChange={handlechange}
          />
          <label for={data[i].option1}>{data[i].option1}</label>
        </div>
        <div>
          <input
            type="radio"
            name="category"
            checked={radiochecked2}
            onClick={() => setRadiochecked2(true)}
            value={data[i].option2}
            onChange={handlechange}
          />
          <label for={data[i].option2}>{data[i].option2}</label>
        </div>
        <div>
          <input
            type="radio"
            name="category"
            checked={radiochecked3}
            onClick={() => setRadiochecked3(true)}
            value={data[i].option3}
            onChange={handlechange}
          />
          <label for={data[i].option3}>{data[i].option3}</label>
        </div>
        <div>
          <input
            type="radio"
            name="category"
            checked={radiochecked4}
            onClick={() => setRadiochecked4(true)}
            value={data[i].option4}
            onChange={handlechange}
          />
          <label for={data[i].option4}>{data[i].option4}</label>
        </div>
      </form>
      <button onClick={handlenext}>Next</button>

      {score}
      {/* <input type="radio">assert</input>
      <input type="radio">nonlocal</input>
      <input type="radio">pass</input> */}
    </div>
  );
}

export default Quetions;
