import React from 'react'
// import Conditional from './conditional.js'

class Footer extends React.Component {
    constructor() {
        super()
        this.state = {
            var: 1,
            isLoading : true,
            text: 'in',
            firstname: '',
            age: '',
            randomImg:"https://i.imgflip.com/1bij.jpg",
            allmemeimg:[]
        }
        this.Change = this.Change.bind(this)
        this.Handle = this.Handle.bind(this)
        this.handleClick=this.handleClick.bind(this)
    }
    componentDidMount(){
        setTimeout(()=>{
            this.setState({isLoading : false})
        },2000)
        fetch("https://api.imgflip.com/get_memes")
            .then(response => response.json())
            .then(response=> {
                const {memes} = response.data
                this.setState({allmemeimg:memes})
            })
    }
    Handle=event=>{
        const {name,value}=event.target
        this.setState({
            [name]:value
        })
    }
    handleClick(){
        const randNum =Math.floor(Math.random()*this.state.allmemeimg.length)
        const randmemeimg=this.state.allmemeimg[randNum].url
        this.setState({randomImg:randmemeimg})
    }
    Change=()=>{
        this.setState(prevState => {
           return (
               {var: prevState.var +1})
        })
    }
    Out=()=>{
        {this.state.text==='in'?this.setState({text:'out'}):this.setState({text:'in'})}
    }
    render() {
        return (
            <div id='footer'>
                <h1>{this.state.var}</h1>
                <button onClick={this.Change}>Change</button>
                {/* <Conditional isLoading={this.state.isLoading}/> */}
                {this.state.isLoading?<h1>Loading..</h1>:<h1>Hey, I am Keshav Garg</h1>}
                <button onClick={this.Out}>Log{this.state.text}</button>
                {this.state.text==='in'?<h3>You are LoggedOut</h3>:<h3>You are loggedIn</h3>}
                <form>
                    <input name='firstname' value={this.state.firstname} placeholder="Enter your name" onChange={this.Handle} />
                    <input type="number" name='age' value={this.state.age} placeholder="Enter your age" onChange={this.Handle} />
                </form>
                <h1>Hey, {this.state.firstname} aged {this.state.age}</h1>
                <div id="gen">
                    <img src={this.state.randomImg} alt='' id='Memeimg'></img>
                    <button onClick={this.handleClick} id='genbtn'>Generate</button>

                </div>
            </div>
        )
    }
}

export default Footer
