import React, { useState } from "react";

function NewCustomer(props) {
  // const {Name,Edu} = props.location.data
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [balance, setBalance] = useState();

  const submitData = () => {
    if (name === "") {
      alert("Enter Name");
    } else if (email === "") {
      alert("Enter Email Id");
    } else if (balance === "") {
      alert("Deposit Some Balance");
    } else {
      fetch("http://192.168.48.50:3000/send", {
        method: "post",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          name,
          email,
          Current_Bal: balance,
        }),
      })
        .then((res) => res.json)
        .then((data) => {
          alert("Successfully registered");
          window.location = "/";
        })
        .catch((err) => {
          alert("Something went wrong");
        });
    }
  };
  return (
    <div className="newbox">
      <div className="header">
        <span className="span1">New Customer Details</span>
      </div>
      <form
        style={{
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <input
          type="text"
          placeholder="Customer Name"
          className="input1"
          value={name}
          onChange={(event) => setName(event.target.value)}
        />
        <input
          type="email"
          placeholder="Customer Email Id"
          className="input1"
          value={email}
          onChange={(event) => setEmail(event.target.value)}
        />
        <input
          type="number"
          placeholder="Deposit Balance"
          className="input1"
          value={balance}
          onChange={(event) => setBalance(event.target.value)}
        />
      </form>
      <div className="box2">
        <button className="btn" onClick={submitData}>
          Submit Details
        </button>
      </div>
    </div>
  );
}

export default NewCustomer;
