import React, { useEffect, useState } from "react";
import { AutoSuggest } from "react-autosuggestions";

function TransferMoney(props) {
  const [makefrom, setMakefrom] = useState();
  const [maketo, setMaketo] = useState();
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch("http://192.168.48.50:3000/")
      .then((res) => res.json())
      .then((results) => {
        console.log(results);
        setData(results);
      });
  }, []);
  const len = data.length;
  const Names = [];
  for (let i = 0; i < len; i++) {
    Names.push(data[i].name);
  }

  return (
    <div>
      <div className="header">
        <span className="span1">Transfer Money</span>
      </div>
      <form>
        <div className="box3">
          <AutoSuggest
            name="From"
            options={Names}
            handleChange={setMakefrom}
            value={makefrom}
            theme={styles}
          />
          <AutoSuggest
            name="To"
            options={Names}
            handleChange={setMaketo}
            value={maketo}
            styles={{
              searchLabel: {
                display: "block",
                fontSize: "1.59rem",
              },
              suggestionOption: {
                margin: "0",
                padding: ".5rem",
                fontSize: "1.35rem",
                whiteSpace: "nowrap",
                overflow: "hidden",
                cursor: "default",
              },
            }}
          />
        </div>
      </form>
    </div>
  );
}

export default TransferMoney;
