import React, { useEffect, useState } from "react";
import { AutoSuggest } from "react-autosuggestions";

function TransferMoney(props) {
  const [makefrom, setMakefrom] = useState("");
  const [maketo, setMaketo] = useState("");
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch("http://192.168.48.50:3000/")
      .then((res) => res.json())
      .then((results) => {
        console.log(results);
        setData(results);
      });
  }, []);
  const len = data.length;
  const Names = [];
  for (let i = 0; i < len; i++) {
    Names.push(data[i].name);
  }

  return (
    <div>
      <div className="header">
        <span className="span1">Transfer Money</span>
      </div>
      <div className="box3">
        <div className="autosuggest">
          <AutoSuggest
            name="From"
            options={Names}
            handleChange={setMakefrom}
            value={makefrom}
          />
        </div>
        <div className="autosuggest">
          <AutoSuggest
            name="To"
            options={Names}
            handleChange={setMaketo}
            value={maketo}
          />
        </div>
      </div>
      <div className="inputamount">
        <text style={{ fontSize: 20, color: "#777" }}>Amount</text>
        <input type="Number" className="inputamt" />
      </div>
    </div>
  );
}

export default TransferMoney;
