import React, { useEffect, useState } from "react";

function Quetions(props) {
  let data = [
    {
      quetion: "Which of the following commands will create a list?",
      option1: "list1 = list()",
      option2: "list1 = []",
      option3: "list1 = list([1, 2, 3])",
      option4: "all of the mentioned",
      ans: "all of the mentioned",
      time: 60,
    },
    {
      quetion: "What is the output when we execute list(“hello”)?",
      option1: "[‘h’, ‘e’, ‘l’, ‘l’, ‘o’]",
      option2: " [‘hello’]",
      option3: " [‘llo’]",
      option4: "[‘olleh’]",
      ans: "[‘h’, ‘e’, ‘l’, ‘l’, ‘o’]",
      time: 30,
    },
  ];
  const [res, setRes] = useState("");
  const [score, setScore] = useState(0);
  const [radiochecked1, setRadiochecked1] = useState(false);
  const [radiochecked2, setRadiochecked2] = useState(false);
  const [radiochecked3, setRadiochecked3] = useState(false);
  const [radiochecked4, setRadiochecked4] = useState(false);
  const [i, setI] = useState(0);
  const [counter, setCounter] = React.useState(data[i].time);
  const [userdata, setUserdata] = useState("");
  let len = data.length;

  useEffect(() => {
    fetch("http://127.0.0.1:3000/")
      .then((res) => res.json())
      .then((results) => {
        setUserdata(results);
      });
  }, []);

  const lenofuserdata = userdata.length;

  if (lenofuserdata != 0) {
    data = userdata;
    len = data.length;
  }

  useEffect(() => {
    window.addEventListener("popstate", (event) => {
      alert("Submited");
      props.history.push({
        pathname: "/result",
        state: { score: score, ans: data[i].ans, res: res },
      });
    });
    // return () => {
    //   window.removeEventListener("popstate", onBackButtonEvent);
    // };
  }, []);

  useEffect(() => {
    const timer = setInterval(() => setCounter(counter - 1), 1000);
    if (counter === 0) {
      handlenext();
    }
    return () => clearInterval(timer);
  }, [counter]);

  const handlechange = (event) => {
    setRes(event.target.value);
  };

  const handlenext = () => {
    if (data[i].ans === res) {
      setScore(score + 1);
    }
    if (i === len - 1) {
      props.history.push({
        pathname: "/result",
        state: { score: score, ans: data[i].ans, res: res },
      });
    }

    setI(i + 1);
    setCounter(data[i].time);
    setRadiochecked1(false);
    setRadiochecked2(false);
    setRadiochecked3(false);
    setRadiochecked4(false);
    setRes("");
  };

  return (
    <div className="container">
      <div className="timeleft">
        <div>Time Left : {counter}</div>
      </div>
      <div className="box2">
        <div>
          <div className="cssquetion">{data[i].quetion}</div>
          <form className="options">
            <div className="box3">
              <input
                type="radio"
                className="radioinputs"
                name="category"
                checked={radiochecked1}
                onClick={() => {
                  setRadiochecked1(true);
                  setRadiochecked2(false);
                  setRadiochecked3(false);
                  setRadiochecked4(false);
                }}
                value={data[i].option1}
                onChange={handlechange}
              />
              <label for={data[i].option1} className="choices">
                {data[i].option1}
              </label>
            </div>
            <div className="box3">
              <input
                type="radio"
                className="radioinputs"
                name="category"
                checked={radiochecked2}
                onClick={() => {
                  setRadiochecked1(false);
                  setRadiochecked2(true);
                  setRadiochecked3(false);
                  setRadiochecked4(false);
                }}
                value={data[i].option2}
                onChange={handlechange}
              />
              <label for={data[i].option2} className="choices">
                {data[i].option2}
              </label>
            </div>
            <div className="box3">
              <input
                type="radio"
                className="radioinputs"
                name="category"
                checked={radiochecked3}
                onClick={() => {
                  setRadiochecked1(false);
                  setRadiochecked2(false);
                  setRadiochecked3(true);
                  setRadiochecked4(false);
                }}
                value={data[i].option3}
                onChange={handlechange}
              />
              <label for={data[i].option3} className="choices">
                {data[i].option3}
              </label>
            </div>
            <div className="box3">
              <input
                type="radio"
                className="radioinputs"
                name="category"
                checked={radiochecked4}
                onClick={() => {
                  setRadiochecked1(false);
                  setRadiochecked2(false);
                  setRadiochecked3(false);
                  setRadiochecked4(true);
                }}
                value={data[i].option4}
                onChange={handlechange}
              />
              <label for={data[i].option4} className="choices">
                {data[i].option4}
              </label>
            </div>
          </form>
          <button onClick={handlenext} className="nextbtn">
            {i === len - 1 ? "Submit" : "Next"}
          </button>
        </div>
      </div>
    </div>
  );
}

export default Quetions;
