import React from 'react'

function Content(props){
    const date= new Date
    const hours = date.getHours()

    const Style={
        fontSize: 40
    }

    let timeofday
    if (hours <12){
        timeofday='Morning'
        Style.color='blue'
    }
    else if (hours<17){
        timeofday='Afternoon'
        Style.color='red'
    }
    else{
        timeofday="Night"
        Style.color='green'
    }
    return(
        <div>
    <div>
        <h1 style={Style}>Good {timeofday}</h1>
    </div>
    <div>
        <h2>{props.quetion}</h2>

    </div>
    </div>
    )
}

export default Content
